base_url = "https://china.caixin.com/"

import re
from bs4 import BeautifulSoup
from urllib.parse import urljoin, urlparse

def extract_article_links_(html, base_url, patterns=None):
    """
    提取通用新闻/文章链接，支持相对与绝对链接匹配

    :param html: 页面 HTML 内容
    :param base_url: 基础网址
    :param patterns: 链接路径正则列表（仅对路径部分匹配）
    :return: List[str] 文章链接列表
    """
    soup = BeautifulSoup(html, 'html.parser')
    a_tags = soup.find_all('a', href=True)

    # 默认支持三类网站结构
    default_patterns = [
        r'^/news/\d+\.html$',                     # 第一财经
        r'^/p/\d+$',                              # 36氪
        r'^/\d{4}-\d{2}-\d{2}/\d+\.html$',        # 财新网 yyyy-mm-dd/数字.html
    ]

    patterns = patterns or default_patterns
    article_links = set()

    for a in a_tags:
        href = a['href'].strip()

        # 统一处理为完整 URL
        full_url = urljoin(base_url, href)

        # 仅提取 path（忽略域名）用于模式匹配
        parsed = urlparse(full_url)
        path = parsed.path

        for pat in patterns:
            if re.match(pat, path):
                article_links.add(full_url)
                break

    return list(article_links)

def extract_article_links(html, base_url, patterns=None):
    """
    用原生 Python 从 HTML 中提取文章链接（支持相对与绝对路径）
    """
    # 匹配所有 href="..." 链接的值
    hrefs = re.findall(r'href="([^"]+)"', html)

    # 默认支持三种结构：第一财经、36氪、财新
    default_patterns = [
        r'^/news/\d+\.html$',                  # 第一财经
        r'^/p/\d+$',                           # 36氪
        r'^/\d{4}-\d{2}-\d{2}/\d+\.html$',     # 财新
    ]
    patterns = patterns or default_patterns

    links = set()

    for href in hrefs:
        # 判断是否是相对链接（以 / 开头）或完整链接（以 http 开头）
        if href.startswith('/'):
            full_url = base_url.rstrip('/') + href
            path = href  # 直接拿来做路径匹配
        elif href.startswith('http'):
            full_url = href
            # 手动从 href 中提取路径部分
            path = '/' + '/'.join(href.split('/')[3:])  # 去掉协议+域名
        else:
            continue  # 忽略其他不规范链接

        # 匹配路径结构
        for pat in patterns:
            if re.match(pat, path):
                links.add(full_url)
                break

    return list(links)

if __name__ == "__main__":
    # 提取文章链接
    article_links = extract_article_links(html, base_url)
    print(len(article_links))
    # 打印提取到的链接
    for link in article_links:
        print(link)

